import { ConnectionType, ProviderType } from '../constants';
import { NetworkDetails } from '../models';
import { Optional } from '../types';
import {
  ethereum,
  ledgerEthereum,
  ledgerLiveEthereum,
  ropsten,
  rskMainnet,
  rskTestnet,
} from '../wallets/bip44/paths';

const defaultNetworks = [
  new NetworkDetails(
    1,
    'ETH',
    ConnectionType.MAINNET,
    'ETH',
    'https://rpc.eth.sovryn.app',
  )
    .setLogo(
      'data:image/svg+xml;base64,',
    )
    .setWalletDPaths(ProviderType.LEDGER, [ledgerEthereum, ledgerLiveEthereum])
    .setWalletDPaths(ProviderType.TREZOR, [ethereum]),
  // Ropsten deprecated, use rinkeby instead
  new NetworkDetails(
    3,
    'Ropsten',
    ConnectionType.TESTNET,
    'TROP',
    'https://rinkeby-light.eth.linkpool.io',
  )
    .setLogo(
      'data:image/svg+xml;base64,',
    )
    .setWalletDPaths(ProviderType.LEDGER, [
      ropsten,
      ethereum,
      ledgerEthereum,
      ledgerLiveEthereum,
    ])
    .setWalletDPaths(ProviderType.TREZOR, [ropsten, ethereum]),
  new NetworkDetails(
    30,
    'RSK',
    ConnectionType.MAINNET,
    'RBTC',
    'https://public-node.rsk.co',
  )
    .setLogo(
      'data:image/svg+xml;base64,',
    )
    .setWalletDPaths(ProviderType.LEDGER, [rskMainnet])
    .setWalletDPaths(ProviderType.TREZOR, [rskMainnet]),
  new NetworkDetails(
    31,
    'RSK Testnet',
    ConnectionType.TESTNET,
    'tRBTC',
    'https://public-node.testnet.rsk.co',
  )
    .setLogo(
      'data:image/svg+xml;base64,',
    )
    .setWalletDPaths(ProviderType.TREZOR, [rskTestnet, rskMainnet])
    .setWalletDPaths(ProviderType.LEDGER, [rskTestnet, rskMainnet]),
];

export class NetworkDictionary {
  private _networks: NetworkDetails[] = [];

  constructor(networks: NetworkDetails[] = defaultNetworks) {
    this._networks = networks;
  }

  public set(networks: NetworkDetails[]) {
    this._networks = networks;
    return this;
  }

  public add(network: NetworkDetails | NetworkDetails[]) {
    if (Array.isArray(network)) {
      this._networks.push(...network);
    } else {
      this._networks.push(network);
    }
    return this;
  }

  public list(): NetworkDetails[];
  public list(networkType: ConnectionType): NetworkDetails[];
  public list(networkType?: ConnectionType): NetworkDetails[] {
    if (networkType !== undefined) {
      return this._networks.filter(
        item => item.getNetworkType() === networkType,
      );
    }
    return this._networks;
  }

  public get(chainId: number): Optional<NetworkDetails> {
    return this._networks.find(item => item.getChainId() === chainId);
  }
}
